import React, {useState, useEffect} from "react";
import axios from "axios";

const App = () => {


    return (
        <div style={
            {
                textAlign: "center",
                padding: "20px"
            }
        }>
            <h1>Hava Məlumatları</h1>
            <input type="text" placeholder="Şəhər daxil edin"
                value={location}
                onChange={
                    (e) => setLocation(e.target.value)
                }
                style={
                    {
                        padding: "10px",
                        fontSize: "16px"
                    }
                }/>
            <button onClick={
                    () => fetchWeatherByCity(location)
                }
                style={
                    {
                        marginLeft: "10px",
                        padding: "10px 20px"
                    }
            }>
                Axtar
            </button>

            {
            error && <p style={
                {color: "red"}
            }>
                {error}</p>
        }

            {
            weatherData && (
                <div style={
                    {marginTop: "20px"}
                }>
                    <h2>{
                        weatherData.name
                    }</h2>
                    <p>Temperatur: {
                        weatherData.main.temp
                    }°C</p>
                    <p>Hava Şərtləri: {
                        weatherData.weather[0].description
                    }</p>
                    <p>Rütubət: {
                        weatherData.main.humidity
                    }%</p>
                </div>
            )
        } </div>
    );
};

export default App;
