import React, {useState, useEffect} from "react";
import axios from "axios";

const App = () => {
    const [location, setLocation] = useState("");
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState("");
    const API_KEY = "9589fed1e485f8c0b62e0f328612ccaf3";
    useEffect(() => {
        navigator.geolocation.getCurrentPosition((position) => {
            const {latitude, longitude} = position.coords;
            fetchWeatherByCoords(latitude, longitude);
        }, () => {
            setError("Unable to retrieve location.")
        })
    }, []);

    const fetchWeatherByCity = async (city) => {
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${API_KEY}`);
            setWeatherData(response.data);
            setError("");
        } catch (err) {
            setError("Şəhər tapılmadı. Zəhmət olmasa yenidən yoxlayın.");
        }
    };

    // Koordinatlara görə hava məlumatlarını gətir
    const fetchWeatherByCoords = async (lat, lon) => {
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=${API_KEY}`);
            setWeatherData(response.data);
            setError("")

        } catch (error) {
            setError("Coordinates are not found")
        }

    };

    return (
        <div style={
            {
                textAlign: "center",
                padding: "20px"
            }
        }>
            <h1>Hava Məlumatları</h1>
            <input type="text" placeholder="Şəhər daxil edin"
                value={location}
                onChange={
                    (e) => setLocation(e.target.value)
                }
                style={
                    {
                        padding: "10px",
                        fontSize: "16px"
                    }
                }/>
            <button onClick={
                    () => fetchWeatherByCity(location)
                }
                style={
                    {
                        marginLeft: "10px",
                        padding: "10px 20px"
                    }
            }>
                Axtar
            </button>

            {
            error && <p style={
                {color: "red"}
            }>
                {error}</p>
        }

            {
            weatherData && (
                <div style={
                    {marginTop: "20px"}
                }>
                    <h2>{
                        weatherData.name
                    }</h2>
                    <p>Temperatur: {
                        weatherData.main.temp
                    }°C</p>
                    <p>Hava Şərtləri: {
                        weatherData.weather[0].description
                    }</p>
                    <p>Rütubət: {
                        weatherData.main.humidity
                    }%</p>
                </div>
            )
        } </div>
    );
};

export default App;
